import React from 'react'
import './Footer.css'
export default class Footer extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      selected1:true,
      selected2:false,
      selected3:false,
      selected4:false,
    }

  }
componentDidMount(){

}
 changeimg(selected,id){
 document.getElementById(id).style.color='red';


  if(id==1){

    this.setState({selected1:!selected})

  }else if(id==2){
    this.setState({selected2:!selected})
  }

 }

  render() {
	  return (
	  <div >
	  <ul className='footbar'>
      <li onClick={this.changeimg.bind(this,this.state.selected1,"1")}  id='1'><p>
        <img src={this.state.selected1?require("./imgs/logo1.png"):require("./imgs/logo2.png")} alt=""/></p>
	  <p style={{color:this.state.selected1?'blue':'red'}}>首页</p></li>
      <li onClick={this.changeimg.bind(this,this.state.selected1,"li2")}  id='li2'><p><img src={this.state.selected2?require("./imgs/logo1.png"):require("./imgs/logo2.png")} alt=""/></p>
      <p style={{color:this.state.selected2?'blue':'rgb(142,142,147)'}}>发现</p></li>
      <li onClick={this.changeimg.bind(this,this.state.selected1,"li3")}  id='li3'><p><img src={this.state.selected3?require("./imgs/logo1.png"):require("./imgs/logo2.png")} alt=""/></p>
      <p style={{color:this.state.selected3?'blue':'rgb(142,142,147)'}}>订单</p></li>
      <li onClick={this.changeimg.bind(this,this.state.selected1,"li4")}  id='li4'><p><img src={this.state.selected4?require("./imgs/logo1.png"):require("./imgs/logo2.png")} alt=""/></p>
      <p style={{color:this.state.selected4?'blue':'rgb(142,142,147)'}}>我的</p></li>
    </ul>
	  </div>
	  )
  }
}
